        float yaw = atan2(data.gyro_y, data.gyro_x) * 180 / PI;

        // è®¡ç®—æ§åˆ¶è¾“å‡º
        float roll_output = pid_compute(&roll_pid, roll);
        float pitch_output = pid_compute(&pitch_pid, pitch);
