  let numerator = NDF * G * F;
  let denominator = 4.0 * NdotV * NdotL + 0.0001;
  let specular = numerator / denominator;

  // æ¼«åå°„é¡¹
  let kS = F;
  let kD = vec3<f32>(1.0) - kS;
  kD *= 1.0 - material.metallic;

